text.script:
'use strict';

export var scriptProperties = createScriptProperties()
	.addCheckbox({
		name: 'active',
		label: 'FPS counter (ON/OFF)',
		value: true
	})
	.addSlider({
		name: 'refreshInterval',
		label: 'Refresh interval (in milliseconds)',
		value: 500,
		min: 0,
		max: 1000,
		integer: false
	})
	.addText({
		name: 'label',
		label: 'Label',
		value: 'fps:'
	})
	.finish();

var currentFrame, oldFrame = 0, fps, totFps = 0, oldTime = 0, n = 0;

/**
 * @param {string} value - for property 'text'
 * @return {string} - update current property value
 */
export function update(value) {
	if (scriptProperties.active){
		let time = new Date();
		if (oldFrame == 0){
			oldFrame = time.getTime();
			oldTime = oldFrame;
		}
		currentFrame = time.getTime();
		if (currentFrame > oldFrame){
			fps = 1000 / (currentFrame - oldFrame);
			oldFrame = currentFrame;
			totFps += fps;
			n++;
			if (currentFrame - oldTime >= parseInt(scriptProperties.refreshInterval)){
				totFps /= n;
				value = scriptProperties.label + ' ' + Math.round(totFps);
				oldTime = currentFrame;
				n = 0;
				totFps = 0;
			}
		}
		return value;
	}
}

