'use strict';

let shichenC = {1:'凌晨',2:'夜间',3:'早晨',4:'上午',5:'中午',6:'下午',7:'傍晚',8:'晚上'};
let shichenY = {1:'Before dawn',2:'At night',3:'Morning',4:'Morning',5:'Noon',6:'Afternoon',7:'Evening',8:'Night'};
let kong

export function update(value) {
	let hour = new Date().getHours();
	let timeTag = hour< 2 ?'lingchen':hour< 6 ?'yejian':hour< 8 ?'zaochen':hour< 11 ?'shangwu':hour < 13 ?'zhongwu':hour< 17 ?'xiawu':hour< 20 ?'bangwan':'wanshang';
	switch (timeTag) {
		case 'lingchen' : kong = shichenY[1] ;break;
		case 'yejian' : kong = shichenY[2] ;break;
		case 'zaochen' : kong = shichenY[3] ;break;
		case 'shangwu' : kong = shichenY[4] ;break;
		case 'zhongwu' : kong = shichenY[5] ;break;
		case 'xiawu' : kong = shichenY[6] ;break;
		case 'bangwan' : kong = shichenY[7] ;break;
		case 'wanshang' : kong = shichenY[8] ;break;
	}
	value = kong
	//console.log(value);
	return value

}

