  delimiterValue = scriptProperties.addDelimiter
}
  if (scriptProperties.useDelimiter == false){
   delimiterValue = [
  ' '
]
}
if (scriptProperties.alignVertical == true){
   newLine = [
  '\n'
]
}
if (scriptProperties.alignVertical == false){
   newLine = [
