// 使用严格模式
'use strict';

// 创建脚本属性
export var scriptProperties = createScriptProperties ()
  // 添加 X 坐标的滑块属性
  .addSlider ( {
    name: 'x', // 属性名称
    label: 'X', // 属性标签
    value: 0.5, // 属性默认值
    min: 0, // 滑块最小值
    max: 1, // 滑块最大值
    integer: false // 是否使用整数
  })
  // 添加 Y 坐标的滑块属性
  .addSlider ( {
    name: 'y', // 属性名称
    label: 'Y', // 属性标签
    value: 0.5, // 属性默认值
    min: 0, // 滑块最小值
    max: 1, // 滑块最大值
    integer: false // 是否使用整数
  })
  // 完成脚本属性的创建
  .finish ();

/**
 * 更新组件的坐标值
 * @param {Vec3} value - 组件的原始坐标值
 * @return {Vec3} - 更新后的坐标值
 */
export function update (value) {
  // 将 X 坐标乘以画布的宽度
  value.x = scriptProperties.x * engine.canvasSize.x;
  // 将 Y 坐标乘以画布的高度
  value.y = scriptProperties.y * engine.canvasSize.y;
  // 返回更新后的坐标值
  return value;
}
